// ScrollBox.js
import React from 'react';
import './ScrollBox.css';
import { Element } from 'react-scroll';

// ScrollBox.js
const ScrollBox = ({ id, platform}) => (
  <Element name={id}>
    <div className="scroll-box">
      {posts.map((post, index) => (
        <div key={index} className="post">
          <p>{post.text}</p>
        </div>
      ))}
    </div>
  </Element>
);

export default ScrollBox;
