import React, { useEffect, useState } from 'react';
import ScrollBox from './sytling/ScrollBox';

function App() {
  const [profile, setProfile] = useState({});
  const [posts, setPosts] = useState([]);
  const [instagramProfile, setInstagramProfile] = useState({});

  useEffect(() => {
    async function fetchProfile() {
      try {
        const response = await fetch('http://localhost:5000/auth/twitter');
        const data = await response.json();
        if (data.redirect_url) {
            window.location.href = data.redirect_url;
        }
      } catch (error) {
          console.error('Error authenticating with Twitter:', error);
      }
    }

    async function fetchCallback() {
      try {
        const response = await fetch('http://localhost:5000/auth/twitter/callback');
        const data = await response.json();
        setProfile(data.user); 
      } catch (error) {
          console.error('Error fetching user profile:', error);
      }
    }

    async function fetchTwitterPosts() {
      try {
        const response = await fetch('http://localhost:5000/auth/twitter/posts'); // Replace with your server-side endpoint
        const data = await response.json();
        setPosts(data.posts); // Assuming your server returns posts data in the expected format
      } catch (error) {
        console.error('Error fetching Twitter posts:', error);
      }
    }

    async function fetchInstagramProfile() {
      try {
        const response = await fetch('http://localhost:5000/instagram/profile'); // Replace with your Instagram profile endpoint
        const data = await response.json();
        setInstagramProfile(data); // Update the Instagram profile state
      } catch (error) {
        console.error('Error fetching Instagram profile:', error);
      }
    }

    fetchProfile();
    fetchCallback();
    fetchTwitterPosts();
    fetchInstagramProfile();
  }, []);

  return (
    <div className="App">
      <h1>Social Media Dashboard</h1>
      <div>
        <h2>Facebook Profile</h2>
          <div>
            <p>qwe</p>
            <p>123</p>
          </div>
        <h2>Twitter Profile
          <div>
            <p>Name: {profile.name}</p>
            <p>Username: {profile.screen_name}</p>
          </div>
        </h2>
        <h2>Instagram Profile
          <div>
            <p>Name: {instagramProfile.username}</p>
          </div>
        </h2>
      </div>
      <div>
        <h2>Posts 欸嘿 der~ der~ der~ der~ der~ der~ der~</h2>
      </div>
      <div className="container">
        <div className="row">
          <div className="scroll-box-container">
            <p className="box-description">Facebook Scroll Box <img src={`Facebook.jpeg`} alt={`Facebook`}/></p>
            <ScrollBox id="FaceBook Box" posts={posts}/>
          </div>
          <div className="scroll-box-container">
            <p className="box-description">Instagram Scroll Box <img src={`Insta.jpeg`} alt={`Insta`}/></p>
            <ScrollBox id="Instagram Box" posts={posts}/>
          </div>
        </div>
        <div className="row">
          <div className="scroll-box-container">
            <p className="box-description">Twitter Scroll Box <img src={`Twitter.jpeg`} alt={`Twitter`}/></p>
            <ScrollBox id="Twitter Box" posts={posts}/>
          </div>
          <div className="scroll-box-container">
            <p className="box-description">Wechat Scroll Box <img src={`Wechat.jpeg`} alt={`Wechat`}/></p>
            <ScrollBox id="Wechat Box" posts={posts}/>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
