import React, { useEffect, useState } from 'react';
import ScrollBox from './sytling/ScrollBox';

function App() {
  const [profile, setProfile] = useState({});
  const [posts, setPosts] = useState([]);
  const [displayChoice, setDisplayChoice] = useState('id'); // 'id' or 'name'


  useEffect(() => {
    async function fetchProfile() {
      try {
        const response = await fetch('http://localhost:5000/facebook/profile');
        const data = await response.json();
        setProfile(data);
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    }

    async function fetchPosts() {
      try {
        const response = await fetch('http://localhost:5000/facebook/posts');
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    }

    fetchProfile();
    fetchPosts();
  }, []);

  return (
    <div className="App">
      <div className="sidebar">
        <button onClick={() => setDisplayChoice('id')}>Display ID</button>
        <button onClick={() => setDisplayChoice('name')}>Display Name</button>
      </div>

      <div className="main-content">
        <h1>Social Media Dashboard</h1>
        <div>
          <h2>Facebook Profile</h2>
          <p>{displayChoice === 'id' ? `ID: ${profile.id}` : `Name: ${profile.name}`}</p>
        </div>
        {/* ... rest of the component ... */}
      </div>
    </div>
  );
}

export default App;
