// ScrollBox.js
import React from 'react';
import './ScrollBox.css';
import { Element } from 'react-scroll';

const ScrollBox = ({ id, platform, content }) => (
  <Element name={id}>
    <div className="scroll-box">
      {/* Add conditional rendering for Facebook platform */}
      {platform === "Facebook" ? 
        <p>{content}</p> :
        <>
          <p>{platform}</p>
          {/* ... other repeated platform paragraphs ... */}
        </>
      }
    </div>
  </Element>
);

export default ScrollBox;
