import React, { useEffect, useState } from 'react';

function App() {
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    async function fetchPosts() {
      try {
        const response = await fetch('/api/posts'); // Fetch data from Flask API
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error('Error fetching data:', error);
      }
    }

    fetchPosts();
  }, []);

  return (
    <div className="App">
      <h1>Social Media Dashboard</h1>
      <div>
        <h2>Posts</h2>
        <ul>
          {posts.map(post => (
            <li key={post.id}>
              <strong>{post.title}</strong>: {post.content}
            </li>
          ))}
        </ul>
      </div>
      {/* Display four scrollable boxes with images */}
      <ScrollBox id="box1" imgSrc="path/to/image1.jpg" />
        <ScrollBox id="box2" imgSrc="path/to/image2.jpg" />
        <ScrollBox id="box3" imgSrc="path/to/image3.jpg" />
        <ScrollBox id="box4" imgSrc="path/to/image4.jpg" />
    </div>
  );
}

export default App;
