import React, { useEffect, useState } from 'react';
import ScrollBox from './sytling/ScrollBox';

function App() {
  const [profile, setProfile] = useState({});
  const [posts, setPosts] = useState([]);
  const [displayChoice, setDisplayChoice] = useState('id'); // 'id' or 'name'

  useEffect(() => {
    async function fetchProfile() {
      try {
        const response = await fetch('http://localhost:5000/facebook/profile');
        const data = await response.json();
        setProfile(data);
      } catch (error) {
        console.error('Error fetching profile:', error);
      }
    }

    async function fetchPosts() {
      try {
        const response = await fetch('http://localhost:5000/facebook/posts');
        const data = await response.json();
        setPosts(data);
      } catch (error) {
        console.error('Error fetching posts:', error);
      }
    }

    fetchProfile();
    fetchPosts();
  }, []);

  const facebookContent = displayChoice === 'id' ? profile.id : profile.name;

  return (
    <div className="App">
      <div className="sidebar">
        <button onClick={() => setDisplayChoice('id')}>Display ID</button>
        <button onClick={() => setDisplayChoice('name')}>Display Name</button>
      </div>

      <div className="main-content">
        <h1>Social Media Dashboard</h1>
        <h1>Social Media Dashboard</h1>
        <div className="container">
          <div className="row">
            <div className="scroll-box-container">
              <p className="box-description">Facebook Scroll Box</p>
              <ScrollBox id="Facebook Box" platform="Facebook" content={facebookContent} />
            </div>
            <div className="scroll-box-container">
              <p className="box-description">Instagram Scroll Box <img src={`Insta.jpeg`} alt={`Insta`} /></p>
              <ScrollBox id="Instagram Box" platform="Instagram" />
            </div>
          </div>
          <div className="row">
            <div className="scroll-box-container">
              <p className="box-description">QQ Scroll Box <img src={`QQ.jpeg`} alt={`QQ`} /></p>
              <ScrollBox id="QQ Box" platform="QQ" />
            </div>
            <div className="scroll-box-container">
              <p className="box-description">Wechat Scroll Box <img src={`Wechat.jpeg`} alt={`Wechat`} /></p>
              <ScrollBox id="Wechat Box" platform="Wechat" />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
